def get_time_interval_stats(start_date, end_date):
    interval_cache = {}
    def get_days_in_month(year, month):
        if (year, month) in interval_cache:
            return interval_cache[(year, month)]
        days_in_month = 30
        if month in [1, 3, 5, 7, 8, 10, 12]:
            days_in_month = 31
        elif month == 2:
            if (year % 4 == 0 and year % 100 != 0) or year % 400 == 0:
                days_in_month = 29
            else:
                days_in_month = 28
        interval_cache[(year, month)] = days_in_month
        return days_in_month
    total_days = 0
    current_date = start_date
    while current_date <= end_date:
        year, month, _ = current_date
        total_days += get_days_in_month(year, month)
        if month < 12:
            current_date = (year, month + 1, 1)
        else:
            current_date = (year + 1, 1, 1)
    return total_days

